import React from "react";
import Header from "./Header";
import Footer from "./Footer";
import Todos from "./Todos";
import AddNewTodo from "./AddNewTodo";
import { useState } from "react";

function App() {

    const addTask = ()=>{
        console.log("This task is gonna be added.");
    }


    const onDelete = (noteItem) => {
        console.log("I am onDelete.", noteItem);

        // deleting this way in react does not work 
        // const index = todoList.indexOf(noteItem);
        // if (index !== -1) {
        // todoList.splice(index, 1);
        // }

        updateList(todoList.filter((e) => {
            return e !== noteItem;
        })
        )
    }
    const [todoList, updateList] = useState([
        { key: 1, title: "do laundry", content: "Wash and fold clothes." },
        { key: 2, title: "check emails", content: "Review and respond to important emails." },
        { key: 3, title: "vacuum floors", content: "Clean floors with a vacuum cleaner." },
        { key: 4, title: "make grocery list", content: "Write down items needed at the grocery store." },
        { key: 5, title: "water plants", content: "Give plants enough water to stay alive." },
        { key: 6, title: "take out trash", content: "Remove garbage and recycling from the home." },
        { key: 7, title: "quick workout", content: "Complete a short exercise routine." },
        { key: 8, title: "pay bills", content: "Make monthly bill payments on time." },
        { key: 9, title: "walk dog", content: "Take your furry friend for a walk." },
        { key: 10, title: "make bed", content: "Straighten sheets and covers on your bed." }
    ]);

    return (
        <div>
            <Header title="Listify" />
            <AddNewTodo addTask={addTask} />
            <Todos todoList={todoList} onDelete={onDelete} />
            <Footer />
        </div>
    );
}

export default App;
